  const [angle, setAngle] = useState(0);

  useEffect(() => {
    let animationId: number;
    let startTime = Date.now();

    const animateCircle = () => {
      const elapsed = Date.now() - startTime;
      const newAngle = (elapsed * 0.0004) % (2 * Math.PI); // 時間で角度を更新

      // 弧の動き（左側を軸として）
      const radius = 50; // 弧の半径（%）
      const centerX = 40; // 左側の軸
      const centerY = 18;

      // 弧の軌道計算（0度から180度の範囲で弧を描く）
      const arcAngle = Math.sin(newAngle) * Math.PI; // 0〜πの範囲

      setCirclePosition({
        x: centerX + Math.cos(arcAngle) * radius,
        y: centerY + Math.sin(arcAngle) * radius * 0.7, // Y軸を少し縮めて弧らしく
      });

      animationId = requestAnimationFrame(animateCircle);
    };

    animationId = requestAnimationFrame(animateCircle);

    return () => {
      if (animationId) {
        cancelAnimationFrame(animationId);
      }
    };
  }, []);
  const pathname = usePathname();
